import { useState } from "react";
import { View, Text, TextInput, Button } from "react-native";
import ResultIMC from "../ResultIMC";


const Form = () => {
    const [height, setHeight] = useState(null);
    const [weight, setWeight] = useState(null);
    const [messageImc, setMessageImc] = useState("preencha o peso e altura");
    const [imc, setImc] = useState(null);
    const [textButton, setTextButton] = useState("Calcular");

    const imcCalculater = () => {
        if (weight != null && height != null) {
            setImc((weight/(height * height)).toFixed(2));
            setHeight(null);
            setWeight(null);
            setTextButton("Calcular novamente");
            setMessageImc("seu IMC é igual:");
            return;
        }
        setImc(null);
        setTextButton("Calcular");
        setMessageImc("preencha o peso e altura");
    };

    return (
        <View>
            <View>
                <Text>Altura</Text>
                <TextInput onChangeText={setHeight} value={height} placeholder="Ex. 1.75" keyboardType="numeric" />
                <Text>Peso</Text>
                <TextInput onChangeText={setWeight} value={weight} placeholder="Ex. 75.365" keyboardType="numeric" />
                <Button onPress={() => imcCalculater()} title={textButton} />
            </View>
            <ResultIMC messageImc={messageImc} imc={imc} />
        </View>
    );
};

export default Form;
