import { useState } from "react";
import { View, Text, TextInput, TouchableOpacity, Vibration, Pressable, Keyboard } from "react-native";
import ResultIMC from "./ResultIMC";
import styles from "./style";


const Form = () => {
    const [height, setHeight] = useState("");
    const [weight, setWeight] = useState("");
    const [messageImc, setMessageImc] = useState("preencha o peso e altura");
    const [imc, setImc] = useState("");
    const [textButton, setTextButton] = useState("Calcular");
    const [errorMessage, setErrorMessage] = useState("");

    const imcCalculater = () => {
        if (weight !== "" && height !== "") {
            setImc((weight/(height.replace(",", ".") * height.replace(",", "."))).toFixed(2));
            setHeight();
            setWeight();
            setTextButton("Calcular novamente");
            setMessageImc("seu IMC é igual:");
            setErrorMessage("");
        }
        Vibration.vibrate();
        setImc();
        setTextButton("Calcular");
        setMessageImc("preencha o peso e altura");
        setErrorMessage("campo obrigatório*");
    };

    return (
        <View style={styles.formContext}>
            {imc == "" ? 
                <Pressable onPress={Keyboard.dismiss} style={styles.form}>
                    <Text style={styles.formLabel}>Altura</Text>
                    <Text style={styles.errorMessage}>{errorMessage}</Text>
                    <TextInput style={styles.formInput} onChangeText={e => setHeight(e.target.value)} value={height} placeholder="Ex. 1.75" keyboardType="numeric" />

                    <Text style={styles.formLabel}>Peso</Text>
                    <Text style={styles.errorMessage}>{errorMessage}</Text>
                    <TextInput style={styles.formInput} onChangeText={e => setWeight(e.target.value)} value={weight} placeholder="Ex. 75.365" keyboardType="numeric" />

                    <TouchableOpacity style={styles.formButton} onPress={() => imcCalculater()}>
                        <Text style={styles.formTextButton}>{textButton}</Text>
                    </TouchableOpacity>
                </Pressable>
            : 
                <View style={styles.exhibitionResultImc}>
                    <ResultIMC messageImc={messageImc} imc={imc} />
                    <TouchableOpacity style={styles.formButton} onPress={() => imcCalculater()}>
                        <Text style={styles.formTextButton}>{textButton}</Text>
                    </TouchableOpacity>
                </View>

            }
        </View>
    );
};

export default Form;
